sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
	if (isLoading || newValue === '' || newValue == oldValue) {
		return;
	}
	var ajax=new GlideAjax('Myclass');
	ajax.addParam('sysparm_name','getSubCat');
	ajax.addParam('sysparm_field_char',newValue);
	ajax.addParam('sysparm_category','u_sub');
	ajax.getXML(ajaxResponse);
	}




function ajaxResponse(serverResponse){
	alert("ajax Response");
	var val=serverResponse.responseXML.documentElement.getAttribute("answer");	

	alert("val : "+val);


	if (val == 'no'){
		alert("no");
			g_form.setMandatory('u_sub' , true);

		}else{
			alert("yes");
			g_form.setMandatory('u_sub' , false);
		}

	alert("fin");
}
