sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading || newValue === '') {
		return;
	}
	var ajax = new GlideAjax('GetCategory');
	ajax.addParam('sysparm_name', 'getCat');
	ajax.addParam('sysparm_fieldname', 'u_sub');
	ajax.addParam('sysparm_dependant_value', newValue);

	ajax.getXML(function(serverResponse) {
		alert("test");
		var result = serverResponse.responseXML.getElementsByTagName("result");
		var favorites = serverResponse.responseXML.getElementsByTagName("favorite");
		var message = result[0].getAttribute("message");

		alert(message);

		if(message == 'non'){

			g_form.setMandatory('u_sub', true);
			//alert("the table" +favorites.length);
			g_form.clearOptions('u_sub');

			for(var i = 0; i < favorites.length; i++) {

				var value = favorites[i].getAttribute("value");
				var label = favorites[i].getAttribute("label");
				alert("test"+value+ ":" +label);

				g_form.addOption('u_sub',value,label);
			}


		}else{
			g_form.setMandatory('u_sub', false);
			g_form.clearOptions('u_sub');
		    g_form.addOption('u_sub','0','--none--');

		}


	});
}
