sys_script_include.script:
var GetCategory = Class.create();
GetCategory.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {


	getCat: function() {

		var result = this.newItem("result");
		var fieldName=this.getParameter('sysparm_fieldname');
		var dependentValue=this.getParameter('sysparm_dependant_value');

		var cat = new GlideRecord("sys_choice");
		//cat.addEncodedQuery("name=x_58872_needit_needit^element=u_sub^dependent_value=hr1");
		cat.addQuery('name','x_58872_needit_needit');
		cat.addQuery('element',fieldName);
		cat.addQuery('dependent_value',dependentValue);
		cat.query();

		if(cat.hasNext()){

			result.setAttribute("message", "non");

			while(cat.next()){

				this._addFavorite(cat.getValue('value'), cat.getValue('label'));

			}




		}else{

			result.setAttribute("message", "yes");
		}

	},

	_addFavorite : function(value, label) {
		var favs = this.newItem("favorite");
		favs.setAttribute("value", value);
		favs.setAttribute("label", label);
	},





	type: 'GetCategory'


});
